let current = 0;
let correctAnswers = 0;
let wrongAnswers = 0;
// 'total' se calculará dentro de DOMContentLoaded o en loadQuestion
// para asegurar que 'questions' ya esté poblado.
// const total = questions.length; // Quitar de aquí

// Declarar las variables de los elementos HTML aquí, pero obtenerlas dentro de DOMContentLoaded
let formContainer;
let scoreDiv;
let correctCounterDiv;
let wrongCounterDiv;
let totalQuestions; // Variable para el total de preguntas

function updateCounters() {
    if (correctCounterDiv) correctCounterDiv.textContent = `Correctas: ${correctAnswers}`;
    if (wrongCounterDiv) wrongCounterDiv.textContent = `Incorrectas: ${wrongAnswers}`;
}

function loadQuestion() {
    if (current < totalQuestions) { // Usar totalQuestions
        const item = questions[current];

        // Verificación para asegurar que 'item' y sus propiedades existen
        if (!item || typeof item.q === 'undefined' || !Array.isArray(item.options)) {
            console.error("Error: La pregunta actual está mal formateada o no existe:", item);
            if (formContainer) formContainer.innerHTML = `<p>Error al cargar la pregunta. Revisa la consola.</p>`;
            return;
        }

        formContainer.innerHTML = `
            <div class="question">
                <h3>${item.q}</h3>
                <div class="options">
                    ${item.options.map((opt, j) =>
                        `<label><input type="radio" name="option" value="${j}"> ${opt}</label>`
                    ).join('')}
                </div>
                <button id="submit-answer">Siguiente</button>
            </div>
        `;

        const submitButton = document.getElementById('submit-answer');
        if (submitButton) {
            submitButton.addEventListener('click', () => {
                const selectedOption = formContainer.querySelector('input[name="option"]:checked');
                if (selectedOption) {
                    const selected = parseInt(selectedOption.value);

                    // Asegúrate de que cada objeto de pregunta tenga una propiedad 'a'
                    if (typeof item.a === 'undefined') {
                        console.error("Error: La pregunta no tiene definida una respuesta correcta (propiedad 'a').", item);
                        // Decide cómo manejar esto, por ejemplo, contarla como incorrecta
                        wrongAnswers++;
                    } else if (selected === item.a) {
                        correctAnswers++;
                    } else {
                        wrongAnswers++;
                    }
                    updateCounters();
                    nextQuestion();
                } else {
                    alert('Por favor selecciona una opción antes de continuar.');
                }
            });
        }
    } else {
        showFinalScore();
    }
}

function nextQuestion() {
    current++;
    loadQuestion();
}

function showFinalScore() {
    if (formContainer) formContainer.innerHTML = '';
    if (scoreDiv) scoreDiv.textContent = `¡Quiz terminado! Puntuación final: ${correctAnswers} correctas de ${totalQuestions} preguntas.`;
}

document.addEventListener('DOMContentLoaded', () => {
    formContainer = document.getElementById('quiz-form');
    scoreDiv = document.getElementById('score');
    correctCounterDiv = document.getElementById('correct-counter');
    wrongCounterDiv = document.getElementById('wrong-counter');

    // Validar que los elementos del DOM existen
    if (!formContainer || !scoreDiv || !correctCounterDiv || !wrongCounterDiv) {
        console.error("Error: Uno o más elementos del DOM no se encontraron. Asegúrate de que los IDs en tu HTML coincidan.");
        return;
    }

    // Validar que el array 'questions' exista y tenga elementos
    if (typeof questions === 'undefined' || !Array.isArray(questions) || questions.length === 0) {
        console.error("Error: El array 'questions' no está definido, no es un array válido, o está vacío.");
        formContainer.innerHTML = "<p>No se pudieron cargar las preguntas. Verifica la definición del array 'questions'.</p>";
        return;
    }

    totalQuestions = questions.length; // Calcular el total aquí

    updateCounters();
    loadQuestion();
});
